
-- какие-то тестовые процессы;
UPDATE process SET 
    create_dt = create_dt + INTERVAL DATEDIFF(CURDATE(), '2013-12-01') DAY,
    status_dt = status_dt + INTERVAL DATEDIFF(CURDATE(), '2013-12-01') DAY
WHERE id >= 1 AND id <= 9;

-- график дежурств, назначение на процесс 10;
UPDATE callboard_task SET 
	time = time + INTERVAL DATEDIFF(CURDATE(), '2016-01-12') DAY;
UPDATE callboard_shift_user SET
	date = date + INTERVAL DATEDIFF(CURDATE(), '2016-01-12') DAY;
UPDATE process SET 
    create_dt = create_dt + INTERVAL DATEDIFF(CURDATE(), '2016-01-12') DAY,
    status_dt = status_dt + INTERVAL DATEDIFF(CURDATE(), '2016-01-12') DAY
WHERE id = 10;
UPDATE param_datetime SET
	value = value + INTERVAL DATEDIFF(CURDATE(), '2016-01-12') DAY
WHERE id=10;

